var Header = require('../packages/header');
var Button = require('../packages/button');
var Cell = require('../packages/cell');
var CellSwipe = require('../packages/cell-swipe');
var Field = require('../packages/field');
var Badge = require('../packages/badge');
var Switch = require('../packages/switch');
var Spinner = require('../packages/spinner');
var TabItem = require('../packages/tab-item');
var TabContainerItem = require('../packages/tab-container-item');
var TabContainer = require('../packages/tab-container');
var Navbar = require('../packages/navbar');
var Tabbar = require('../packages/tabbar');
var Search = require('../packages/search');
var Checklist = require('../packages/checklist');
var Radio = require('../packages/radio');
var Loadmore = require('../packages/loadmore');
var Actionsheet = require('../packages/actionsheet');
var Popup = require('../packages/popup');
var Swipe = require('../packages/swipe');
var SwipeItem = require('../packages/swipe-item');
var Range = require('../packages/range');
var Picker = require('../packages/picker');
var Progress = require('../packages/progress');
var Toast = require('../packages/toast');
var Indicator = require('../packages/indicator');
var MessageBox = require('../packages/message-box');
var InfiniteScroll = require('../packages/infinite-scroll');
var Lazyload = require('../packages/lazyload');
var DatetimePicker = require('../packages/datetime-picker');
var IndexList = require('../packages/index-list');
var IndexSection = require('../packages/index-section');
var PaletteButton = require('../packages/palette-button');
var merge = require('./utils/merge');

const version = '2.2.7';
const install = function(Vue, config = {}) {
    if (install.installed) return;

    Vue.component(Header.name, Header);
    Vue.component(Button.name, Button);
    Vue.component(Cell.name, Cell);
    Vue.component(CellSwipe.name, CellSwipe);
    Vue.component(Field.name, Field);
    Vue.component(Badge.name, Badge);
    Vue.component(Switch.name, Switch);
    Vue.component(Spinner.name, Spinner);
    Vue.component(TabItem.name, TabItem);
    Vue.component(TabContainerItem.name, TabContainerItem);
    Vue.component(TabContainer.name, TabContainer);
    Vue.component(Navbar.name, Navbar);
    Vue.component(Tabbar.name, Tabbar);
    Vue.component(Search.name, Search);
    Vue.component(Checklist.name, Checklist);
    Vue.component(Radio.name, Radio);
    Vue.component(Loadmore.name, Loadmore);
    Vue.component(Actionsheet.name, Actionsheet);
    Vue.component(Popup.name, Popup);
    Vue.component(Swipe.name, Swipe);
    Vue.component(SwipeItem.name, SwipeItem);
    Vue.component(Range.name, Range);
    Vue.component(Picker.name, Picker);
    Vue.component(Progress.name, Progress);
    Vue.component(DatetimePicker.name, DatetimePicker);
    Vue.component(IndexList.name, IndexList);
    Vue.component(IndexSection.name, IndexSection);
    Vue.component(PaletteButton.name, PaletteButton);
    Vue.use(InfiniteScroll);
    Vue.use(Lazyload, merge({
        loading: require('./assets/loading-spin.svg'),
        attempt: 3
    }, config.lazyload));

    Vue.$messagebox = Vue.prototype.$messagebox = MessageBox;
    Vue.$toast = Vue.prototype.$toast = Toast;
    Vue.$indicator = Vue.prototype.$indicator = Indicator;
};

// auto install
if (typeof window !== 'undefined' && window.Vue) {
    install(window.Vue);
};

module.exports = {
    install,
    version,
    Header,
    Button,
    Cell,
    CellSwipe,
    Field,
    Badge,
    Switch,
    Spinner,
    TabItem,
    TabContainerItem,
    TabContainer,
    Navbar,
    Tabbar,
    Search,
    Checklist,
    Radio,
    Loadmore,
    Actionsheet,
    Popup,
    Swipe,
    SwipeItem,
    Range,
    Picker,
    Progress,
    Toast,
    Indicator,
    MessageBox,
    InfiniteScroll,
    Lazyload,
    DatetimePicker,
    IndexList,
    IndexSection,
    PaletteButton
};